import React, { useState, useEffect, useCallback } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles.css';
import { Container, Row, Col, Button, InputGroup, Input, FormGroup, Label } from 'reactstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
    const navigate = useNavigate();
    const [games, setGames] = useState([]);
    const [filters, setFilters] = useState({
        platform: '',
        genre: '',
        developer: '',
        developerLocation: '',
        publisher: '',
        publisherLocation: '',
        score: '',
        reviewerName: '',
        reviewerAffiliation: ''
    });
    const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5021/api/Games')
      .then(response => setGames(response.data))
      .catch(error => console.error('Error fetching games:', error));
  }, []);

  const handleViewMoreClick = (gameId) => {
    navigate(`/game/${gameId}`);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };

  const deleteGame = async (gameId) => {
    try {
      await axios.delete(`http://localhost:5021/api/Games/${gameId}`);
      setGames(games.filter(game => game.gameID !== gameId));
    } catch (error) {
      console.error('Error deleting the game:', error);
    }
  };


  const filteredGames = games.filter(game => {
    return (
      (filters.platform ? game.platform.toLowerCase().includes(filters.platform.toLowerCase()) : true) &&
      (filters.genre ? game.genre.toLowerCase().includes(filters.genre.toLowerCase()) : true) &&
      (filters.developer ? game.developer.name.toLowerCase().includes(filters.developer.toLowerCase()) : true) &&
      (filters.developerLocation ? game.developer.location.toLowerCase().includes(filters.developerLocation.toLowerCase()) : true) &&
      (filters.publisher ? game.publisher.name.toLowerCase().includes(filters.publisher.toLowerCase()) : true) &&
      (filters.publisherLocation ? game.publisher.headquarters.toLowerCase().includes(filters.publisherLocation.toLowerCase()) : true) &&
      (filters.score ? game.gameReviews.some(review => review.score.toString() === filters.score) : true) &&
      (filters.reviewerName ? game.gameReviews.some(review => review.reviewer.name.toLowerCase().includes(filters.reviewerName.toLowerCase())) : true) &&
      (filters.reviewerAffiliation ? game.gameReviews.some(review => review.reviewer.affiliation.toLowerCase().includes(filters.reviewerAffiliation.toLowerCase())) : true)
    );
  }).filter(game => {
    return game.title.toLowerCase().includes(searchTerm.toLowerCase());
  });

  return (
    <Container>
      <div style={{ backgroundColor: '#FFA500', padding: '10px' }}>
        <h1 className="text-center my-4">Welcome To The VG Database!</h1>
      </div>

      <div className="d-flex justify-content-between align-items-center mb-4">
        <InputGroup>
          <Input
            placeholder="Type to search the database..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </InputGroup>
        <Button color="info" onClick={() => navigate('/add-game')} style={{ marginLeft: '8px' }}>Add Game</Button>
      </div>

      <div className="mb-4">
        <FormGroup>
          <Label for="platform">Platform</Label>
          <Input type="text" name="platform" id="platform" value={filters.platform} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="genre">Genre</Label>
          <Input type="text" name="genre" id="genre" value={filters.genre} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="developer">Developer</Label>
          <Input type="text" name="developer" id="developer" value={filters.developer} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="developerLocation">Developer Location</Label>
          <Input type="text" name="developerLocation" id="developerLocation" value={filters.developerLocation} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="publisher">Publisher</Label>
          <Input type="text" name="publisher" id="publisher" value={filters.publisher} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="publisherLocation">Publisher Location</Label>
          <Input type="text" name="publisherLocation" id="publisherLocation" value={filters.publisherLocation} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="score">Score</Label>
          <Input type="number" name="score" id="score" value={filters.score} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="reviewerName">Reviewer Name</Label>
          <Input type="text" name="reviewerName" id="reviewerName" value={filters.reviewerName} onChange={handleFilterChange} />
        </FormGroup>
        <FormGroup>
          <Label for="reviewerAffiliation">Reviewer Affiliation</Label>
          <Input type="text" name="reviewerAffiliation" id="reviewerAffiliation" value={filters.reviewerAffiliation} onChange={handleFilterChange} />
        </FormGroup>
      </div>

      {filteredGames.map(game => (
        <Row key={game.gameID} className="mb-4">
          <Col>
            <div className="p-3 border game-tile">
              <h3>{game.title}</h3>
              <p className="text-muted" style={{ fontSize: 'smaller' }}>
                {game.platform} | Score: {game.gameReviews[0]?.score ?? 'No score'}
              </p>
              <Button color="danger" className="mr-2" onClick={() => deleteGame(game.gameID)}>Remove</Button>
              <Button color="success" style={{ marginLeft: '8px' }} onClick={() => handleViewMoreClick(game.gameID)}>View More</Button>
            </div>
          </Col>
        </Row>
      ))}

      <footer className="text-center" style={{ backgroundColor: '#FFD580', padding: '10px' }}>
        <p>Sabrina Quadir</p>
      </footer>
    </Container>
  );
}

export default HomePage;
