import React, { useState, useEffect, useCallback } from 'react';
import { Container, Row, Col, Button, InputGroup, Input } from 'reactstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Styles.css';

const HomePage = () => {
    const navigate = useNavigate();
    const [games, setGames] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    // Fetch games from the backend when the component mounts
    useEffect(() => {
        axios.get('http://localhost:5021/api/Games')
            .then(response => {
                setGames(response.data);
            })
            .catch(error => console.error('Error fetching games:', error));
    }, []);

    // Handle adding a new game
    const handleAddGameClick = () => {
        navigate('/add-game'); // Navigate to the add game page
    };

    // Handle viewing more details about a game
    const handleViewMoreClick = useCallback((gameId) => {
        navigate(`/game/${gameId}`);
    }, [navigate]);

    // Handle search term change
    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    // Filter games based on the search term
    const filteredGames = searchTerm ? games.filter(game => game.title.toLowerCase().includes(searchTerm.toLowerCase())) : games;

    // Delete a game
    const deleteGame = (gameId) => {
        axios.delete(`http://localhost:5021/api/Games/${gameId}`)
            .then(() => {
                setGames(prevGames => prevGames.filter(game => game.gameID !== gameId));
            })
            .catch(error => console.error('Error deleting the game:', error));
    };

    return (
        <Container>
            <div style={{ backgroundColor: '#FFA500', padding: '10px' }}>
                <h1 className="text-center my-4">Welcome To The VG Database!</h1>
            </div>

            <div className="d-flex justify-content-between align-items-center mb-4">
                <InputGroup>
                    <Input
                        placeholder="Type to search the database..."
                        value={searchTerm}
                        onChange={handleSearchChange}
                    />
                </InputGroup>
                <Button color="info" onClick={handleAddGameClick} style={{ marginLeft: '8px' }}>Add Game</Button>
            </div>

            <Row>
                {filteredGames.map(game => (
                    <Col key={game.gameID} sm="4" className="mb-4">
                        <div className="p-3 border game-tile">
                            <h3>{game.title}</h3>
                            <p className="text-muted" style={{ fontSize: 'smaller' }}>
                                {game.platform} | Score: {game.score || 'N/A'}
                            </p>
                            <Button color="danger" className="mr-2" onClick={() => deleteGame(game.gameID)}>Remove</Button>
                            <Button color="success" style={{ marginLeft: '8px' }} onClick={() => handleViewMoreClick(game.gameID)}>View More</Button>
                        </div>
                    </Col>
                ))}
            </Row>

            <footer className="text-center" style={{ backgroundColor: '#FFD580', padding: '10px' }}>
                <p>Sabrina Quadir</p>
            </footer>
        </Container>
    );
};

export default HomePage;
