import React, { useState, useEffect } from "react";
import "./Styles.css";
import { Container } from "react-bootstrap";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import GameModify from "./GameModify";

const GameDetail = () => {
  const navigate = useNavigate();
  const { gameId } = useParams();
  const [game, setGame] = useState(null);
  const [review, setReview] = useState(null);
  const [developer, setDeveloper] = useState(null);
  const [publisher, setPublisher] = useState(null);
  const [reviewer, setReviewer] = useState(null);
  const [isEditMode, setIsEditMode] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const gameResponse = await axios.get(`http://localhost:5021/api/Games/${gameId}`);
        if (gameResponse.data) {
          setGame(gameResponse.data);
          setDeveloper(gameResponse.data.developer);
          setPublisher(gameResponse.data.publisher);
        }

        const reviewsResponse = await axios.get(`http://localhost:5021/api/GameReviews`);
        if (reviewsResponse.data) {
          const gameReview = reviewsResponse.data.find(review => review.game.gameID.toString() === gameId);
          if (gameReview) {
            setReview(gameReview);
            setReviewer(gameReview.reviewer);
          }
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [gameId]);

  const handleBackClick = () => {
    navigate("/"); // Navigates back to the homepage
  };

  const handleModify = () => {
    setIsEditMode(true);
  };

  const handleSave = async (updatedGame) => {
    try {
      if (!updatedGame || !updatedGame.gameID) {
        throw new Error("The game must have an ID.");
      }

      const gameForSave = {
        ...updatedGame,
        developer: updatedGame.developer || game.developer,
        publisher: updatedGame.publisher || game.publisher
      };

      const response = await axios.put(`http://localhost:5021/api/Games/${updatedGame.gameID}`, gameForSave);

      setGame(response.data);
      setIsEditMode(false);
    } catch (error) {
      console.error("Error saving the updated game details:", error.response ? error.response.data : error.message);
    }
  };

  const handleCancel = () => {
    setIsEditMode(false);
  };

  return (
    <Container>
      <div style={{ backgroundColor: "#FFA500", padding: "10px", textAlign: "center" }}>
        <h1>Welcome To The VG Database!</h1>
      </div>

      {isEditMode ? (
        <GameModify game={game} onSave={handleSave} onCancel={handleCancel} />
      ) : (
        <div style={{ display: "flex", justifyContent: "space-between", padding: "10px" }}>
          <button
            className="button-style"
            onClick={handleBackClick}
            style={{ backgroundColor: "red", color: "white" }}
          >
            Back
          </button>
          <button
            className="button-style"
            onClick={handleModify}
            style={{ backgroundColor: "blue", color: "white" }}
          >
            Modify
          </button>
        </div>
      )}

      {!isEditMode && game && (
        <div className="section">
          <h1>{game.title}</h1>
          <p>
            <b>Release Date: </b>
            {new Date(game.releaseDate).toLocaleDateString()}
          </p>
          <p>
            <b>Genre: </b>
            {game.genre}
          </p>
          <p>
            <b>Platform: </b>
            {game.platform}
          </p>
        </div>
      )}

      {!isEditMode && developer && (
        <div className="section">
          <h2>Developer:</h2>
          <p>
            <b>Name: </b>
            {developer.name}
          </p>
          <p>
            <b>Location: </b>
            {developer.location}
          </p>
          <p>
            <b>Founding Date: </b>
            {new Date(developer.foundingDate).toLocaleDateString()}
          </p>
        </div>
      )}

      {!isEditMode && publisher && (
        <div className="section">
          <h2>Publisher:</h2>
          <p>
            <b>Name: </b>
            {publisher.name}
          </p>
          <p>
            <b>Headquarters: </b>
            {publisher.headquarters}
          </p>
          <p>
            <b>Founding Date: </b>
            {new Date(publisher.foundingDate).toLocaleDateString()}
          </p>
        </div>
      )}

      {!isEditMode && review && (
        <div className="section">
          <h2>Review:</h2>
          <p>
            <b>Score: </b>
            {review.score}
          </p>
          <p>
            <b>Review Text: </b>
            {review.reviewText}
          </p>
          <p>
            <b>Review Date: </b>
            {new Date(review.reviewDate).toLocaleDateString()}
          </p>
          {reviewer && (
            <>
              <h2>Reviewer:</h2>
              <p>
                <b>Name: </b>
                {reviewer.name}
              </p>
              <p>
                <b>Affiliation: </b>
                {reviewer.affiliation}
              </p>
            </>
          )}
        </div>
      )}

      <footer className="text-center" style={{ backgroundColor: "#FFD580", padding: "10px" }}>
        <p>Sabrina Quadir</p>
      </footer>
    </Container>
  );
};

export default GameDetail;
